# ---------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
 
# Set title of screen
pygame.display.set_caption("Artificial Intelligence Final Project Spring 2020")
 
# The flag that allows the maze to loop until the user clicks the close button
done = False
 
# Declaration of a clock variable that utilizes a clock function
# from the pygame library that mediates the speed of the screen updates
clock = pygame.time.Clock()
 
# -------- Main Program Loop -----------
while not done:
    # intialize draw and erase to false
    draw = False
    erase = False
    # Now, if the user makes a mouse click, we need to capture it
    # TODO I think this might be where we will add code to allow the user to choose 
    # the amount of agents in the simulation
    for event in pygame.event.get():  
        # First, if the user clicks the close button, we need to close the window down
        if event.type == pygame.QUIT:
            # by changing the loop flag to True
            done = True
        # Now that we know that this mouse click isn't a close button,
        # if it's a left click of the mouse, color the space clicked on red  
        elif pygame.mouse.get_pressed()[0]:
            draw = True
        # But if the user isn't holding the left mouse button, don't activate the draw function
        elif not pygame.mouse.get_pressed()[0]: 
            draw = False
        # If the user right clicks, erase the obstacle clicked on
        if pygame.mouse.get_pressed()[2]: 
            erase = True
        # But if the user isn't holding the right mouse button, don't activate the erase function
        elif not pygame.mouse.get_pressed()[2]: 
            erase = False
        
    # If the mouse was clicked and not to close the maze, we better determine what the user is trying to ddo
    if draw == True:
        # Begin by capturing the mouse click location
        pos = pygame.mouse.get_pos()
        # Change the x/y screen coordinates to grid coordinates
        # These next 2 lines of code use integer division to divide the 
        # current x/y positioning of the mouse click by the cell size in order to scale the maze 
        # TODO I think this is the area where we will change from the maze being perceptive
        # of mouse clicks to tracking agent movements
        column = pos[0] // (CELL_SIZE)         # Use to be: (CEll_SIZE + MARGIN)
        row = pos[1] // (CELL_SIZE)    # Use to be: (CELL_SIZE + MARGIN)
        # Set that location to one(wall) if cell is within grid boundaries
        if row < 41 and row >= 0 and column >= 0 and column < 70:
            if grid[row][column] == 0:
                grid[row][column] = 1
            #print("Draw at ", pos, "Grid coordinates: ", row, column)
    # We need to allow ourselves to erase erroneous obstacle highlights
    if erase == True:
        # User clicks the mouse. Get the position
        pos = pygame.mouse.get_pos()
        # Change the x/y screen coordinates to grid coordinates
        column = pos[0] // (CELL_SIZE)
        row = pos[1] // (CELL_SIZE)
        # Set that location to zero(open) if cell is within grid boundaries
        if row < 40 and row >= 0 and column >= 0 and column < 70:
            if grid[row][column] == 1:
                grid[row][column] = 0
            #print("Erase at ", pos, "Grid coordinates: ", row, column)
 
    # Set the screen background
    screen.fill(BLACK)
 
    # Draw the maze
    # For every row in the grid
    for row in range(41):
        # And every column as well              
        for column in range(70):
            # Let's make the background black
            color = BLACK
            # Now we iterate through our 2 dimensional array and print obstacle locations in red              
            if MAZE[row][column] == 1:
                color = RED
            # TODO I believe here is where code can be added to draw lines on the diagonal obstacles so that they don't show as jagged
            # The pygame draw.rect function takes 3 primary arguments:
            # The first argument is the surface on which the rectangle will be drawn
            # The second is the desired color of the rectangle
            # The third is a list/tuple with the following values in this order:
            # x coordinate, y coordinate, width of rectangle, Height of rectangle, and the thickness of the rectangle lines
            # If no argument is given for the thickness parameter (like in our case), then the default is to fill the rectangle with the color argument
            pygame.draw.rect(screen,
                             color,
                            # x coordinate is the product of the (cell width + margin) and the (current column + margin)
                             [CELL_SIZE * column,   # Use to have MARGIN added to both CELL_SIZE and column
                             # y coordinate is the product of the (cell height + margin) and the (current row + margin)    
                               CELL_SIZE * row,      # Use to have MARGIN added to both CELL_SIZE and row
                              CELL_SIZE,        # rectangle width
                              CELL_SIZE])      # rectangle height
 
    # Limit to 60 frames per second
    clock.tick(60)
 
    # Go ahead and update the screen with what we've drawn.
    pygame.display.flip()
 
# Be IDLE friendly. If you forget this line, the program will 'hang'
# on exit.
pygame.quit()

